import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{o as t,c as o,d as r}from"./app.82b3fc8e.js";const s={},n=r('<p><img src="https://images.unsplash.com/photo-1611974789855-9c2a0a7236a3?crop=entropy&amp;cs=tinysrgb&amp;fit=max&amp;fm=png&amp;ixid=MnwxNDIyNzR8MHwxfHNlYXJjaHwxfHxzdG9ja3xlbnwwfHx8fDE2MzcwNzM5MjY&amp;ixlib=rb-1.2.1&amp;q=80&amp;w=1080" alt="image" loading="lazy"></p><p>\u592A\u4E45\u6CA1\u6709\u5199\u8FC7\u535A\u5BA2\u4E86\uFF0C\u4ECA\u5929\u8D81G20\u4E2D\u7F8E\u548C\u8C08\u4E4B\u540E\uFF0C\u7F8E\u80A1\u5927\u6DA8\u4E4B\u9645\u8C08\u4E0B\u6700\u8FD1\u6240\u601D\u6240\u60F3\u3002</p><p>\u5148\u4EA4\u4EE3\u4E00\u4E0B\u80CC\u666F\u5427\uFF0C\u6700\u8FD1\u80A1\u5E02\u6CE2\u52A8\uFF0C\u6BCF\u4E2A\u591C\u665A\u90FD\u8BA9\u6211\u5FC3\u60CA\u80C6\u98A4\uFF0C\u5FC3\u795E\u4E0D\u5B81\uFF0C\u65E0\u6CD5\u96C6\u4E2D\u6CE8\u610F\u529B\uFF0C\u8FD9\u6BB5\u65F6\u95F4\u91CC\uFF0C\u6211\u7ECF\u8FC7\u4E86\u591A\u6B21\u7684\u5931\u8BEF\u64CD\u4F5C\u4EE5\u540E\uFF0C\u603B\u8D44\u4EA7\u4E00\u5EA6\u8DCC\u7834\u4E8630%\uFF0C\u5728\u8FD9\u8FF7\u832B\u4E4B\u9645\uFF0C\u6211\u51B3\u5B9A\u5C06\u6211\u7684\u611F\u609F\u5199\u4E0B\u3002</p><p>\u8FD9\u7BC7\u6587\u7AE0\u4E0D\u662F\u60F3\u8C08\u6211\u662F\u600E\u4E48\u7092\u80A1\u4E8F\u94B1\u7684\uFF0C\u800C\u662F\u60F3\u8C08\u4E00\u4E2A\u6709\u5173\u7684\u611F\u609F\u3002</p><p>\u968F\u7740\u65F6\u4EE3\u7684\u8FDB\u6B65\uFF0C\u7269\u8D28\u751F\u6D3B\u7684\u4E30\u5BCC\uFF0C\u4EBA\u7C7B\u53EF\u4EE5\u505A\u51FA\u7684\u9009\u62E9\u4E5F\u662F\u8D8A\u6765\u8D8A\u591A\u6837\u5316\u4E86\uFF0C\u5728\u8FD9\u6709\u591A\u6837\u9009\u62E9\u7684\u4E16\u754C\u91CC\uFF0C\u5927\u5BB6\u662F\u611F\u89C9\u66F4\u5E78\u798F\u4E86\u5462\uFF0C\u8FD8\u662F\u66F4\u7126\u8651\u4E86\u5462\u3002</p><p>\u5C31\u6211\u800C\u8A00\uFF1A\u591A\u6837\u7684\u9009\u62E9\u4F7F\u4EBA\u66F4\u7126\u8651\u3002</p><p>\u4E3A\u4EC0\u4E48\u8FD9\u4E48\u8BF4\u5462\uFF1F</p><p>\u8FD9\u662F\u6211\u4ECE\u4EB2\u8EAB\u4F53\u4F1A\u91CC\u603B\u7ED3\u51FA\u6765\u7684\uFF0C\u5148\u8BF4\u4E2A\u4F8B\u5B50\u5427\uFF1A</p><p>\u4E0A\u5C0F\u5B66\u4E2D\u5B66\u7684\u65F6\u5019\uFF0C\u5FC5\u987B\u8981\u7A7F\u6821\u670D\uFF0C\u6CA1\u5F97\u9009\u62E9\uFF0C\u89C9\u5F97\u7A7F\u6821\u670D\u662F\u4EF6\u5F88\u81EA\u7136\u7684\u4E8B\u60C5\uFF0C\u6BCF\u5929\u8F7B\u677E\u6109\u5FEB\u7684\u5F88\u5FEB\u5C31\u6362\u597D\u4E86\u8863\u670D\u53BB\u4E0A\u5B66\u4E86\u3002</p><p>\u5230\u5927\u5B66\u4EE5\u540E\uFF0C\u6CA1\u6709\u6821\u670D\uFF0C\u8981\u81EA\u5DF1\u4E70\u8863\u670D\u4E86\uFF0C\u5C31\u591A\u4E86\u9009\u62E9\u3002</p><p>\u724C\u5B50\u3001\u989C\u8272\u3001\u5C3A\u5BF8\u3001\u4EF7\u683C\u3001\u8D28\u91CF\u90FD\u53EF\u4EE5\u9009\uFF0C\u9009\u6765\u9009\u53BB\uFF0C\u8D39\u65F6\u8D39\u94B1\u53C8\u8D39\u7CBE\u529B\u3002</p><p>\u9009\u62E9\u591A\u4E86\uFF0C\u4EBA\uFF0C\u4E5F\u5C31\u53D8\u7126\u8651\u4E86\u3002</p><p>\u8FD9\u8FD8\u53EA\u662F\u4E70\u8863\u670D\u7F62\uFF0C\u4F60\u8FD8\u5F97\u9009\u62E9\u6BCF\u5929\u8981\u7A7F\u4EC0\u4E48\u8863\u670D\uFF0C\u8FD8\u8981\u9009\u62E9\u5982\u4F55\u642D\u914D\uFF0C\u8003\u8651\u51FA\u884C\u7684\u573A\u5408\uFF0C\u4E0E\u8C01\u4F1A\u9762\uFF0C\u8003\u8651\u6211\u662F\u4E0D\u662F\u8FD9\u5468\u7A7F\u8FC7\u4E86\u8FD9\u5957\u8863\u670D\uFF0C\u8003\u8651\u4F1A\u4E0D\u4F1A\u548C\u522B\u4EBA\u649E\u886B\uFF0C\u8003\u8651\u592A\u591A\u592A\u591A\u3002\u3002\u3002</p><p>\u591A\u6837\u7684\u9009\u62E9\u7ED9\u751F\u6D3B\u589E\u6DFB\u4E86\u4E00\u4EFD\u8D1F\u62C5\u3002</p><p>\u518D\u4E3E\u4E2A\u80A1\u7968\u7684\u4F8B\u5B50\uFF1A</p><p>\u5148\u8981\u8BF4\u53E5\u5F88\u6709\u4E89\u8BAE\u7684\u8BDD\uFF1A\u76F8\u6BD4\u7092\u7F8E\u80A1\uFF0C\u7092A\u80A1\u662F\u5E78\u798F\u7684\u3002</p><p>\u4F60\u53EF\u80FD\u5728\u7B2C\u4E00\u65F6\u95F4\u5C31\u4F1A\u53CD\u9A73\u6211\u8BF4\u7684\u8FD9\u53E5\u8BDD\uFF0C\u5FC3\u60F3\u4F60\u80AF\u5B9A\u662F\u7F8E\u80A1\u4E8F\u592A\u591A\u94B1\uFF0C\u89C9\u5F97A\u80A1\u6709\u6DA8\u8DCC\u5E45\u9650\u5236\u5C31\u4F1A\u4E8F\u5C11\u4E00\u70B9\uFF0C\u5374\u5FD8\u8BB0\u7F8E\u80A1\u662F\u725B\u5E02\uFF0CA\u80A1\u662F\u718A\u5E02\u7684\u4E8B\u5B9E\u3002</p><p>\u6211\u5462\uFF0C\u5F53\u7136\u662F\u77E5\u9053\u8FD9\u4E00\u70B9\u7684\uFF0C\u4F46\u6211\u60F3\u8BF4\u7684\u5E78\u798F\u5E76\u4E0D\u662F\u5728\u54EA\u597D\u8D5A\u94B1\uFF0C\u5728\u54EA\u4E0D\u597D\u8D5A\u94B1\u7684\u95EE\u9898\uFF0C\u6211\u6BD4\u8F83\u7684\u4F9D\u636E\u662F\u5BF9\u4EBA\u7684\u6298\u78E8\u7A0B\u5EA6\u3002</p><p>\u5728A\u80A1\uFF0C\u4E00\u822C\u7684\u6563\u6237\u662F\u53EA\u80FD\u505A\u591A\u7684\uFF08\u8FD9\u91CC\u8BF4\u7684\u662F\u4E00\u822C\u7684\u6563\u6237\u5566\uFF0C\u522B\u8BA1\u8F83\u4EC0\u4E48\u878D\u8D44\u4EC0\u4E48\u7684\uFF09\uFF0C\u5E76\u4E14\u53EA\u80FDT+1\u4EA4\u6613\uFF0C\u4E5F\u5C31\u662F\u8BF4\u4F60\u5F53\u5929\u4E70\u5165\u8981\u6B21\u65E5\u540E\u624D\u80FD\u5356\u51FA\u3002</p><p>\u7092A\u80A1\u65F6\u6211\u662F\u8FD9\u6837\u7684\uFF1A\u6211\u770B\u597D\u4E00\u652F\u80A1\u7968\uFF0C\u6211<strong>\u552F\u4E00</strong>\u7684\u9009\u62E9\u5C31\u662F\u8981\u5728\u4EC0\u4E48\u4EF7\u4F4D\u4E0A\u4E70\u591A\u5C11\u80A1\u5462\uFF0C\u6211\u5F53\u5929\u4E70\u8FDB\u53BB\u4EE5\u540E\uFF0C\u8FD9\u5929\u7684\u4EA4\u6613\u5C31\u7ED3\u675F\u4E86\uFF0C\u56E0\u4E3A\u6211<strong>\u65E0\u6CD5\u518D\u652F\u914D</strong>\u6211\u4E70\u8FDB\u53BB\u7684\u8FD9\u90E8\u5206\u8D44\u91D1\u4E86\uFF0C\u5B83\u662F\u6DA8\u8FD8\u662F\u8DCC\u6211\u90FD\u53EA\u80FD\u770B\u7740\u3002</p><p>\u4F46\u5728\u7F8E\u80A1\uFF0C\u4E00\u5207\u5C31\u4E0D\u4E00\u6837\u4E86\u3002</p><p>\u7F8E\u80A1\u91CC\u53EF\u4EE5\u505A\u7A7A\u548C\u505A\u591A\uFF0C\u53EF\u4EE5\u8F7B\u6613\u7684\u878D\u8D44\u4E0A\u6760\u6746\uFF0C\u6700\u91CD\u8981\u7684\u662F\u5B83\u53EF\u4EE5T+0\u4EA4\u6613\uFF0C\u4E5F\u5C31\u662F\u8BF4\u4F60\u53EF\u4EE5\u5728\u5F53\u5929\u4E70\u5165\u5F53\u5929\u5356\u51FA\uFF0C\u53CD\u6094\u65E0\u6570\u6B21\uFF08\u6709\u4EE3\u4EF7\u7684\uFF09\u3002</p><p>\u7092\u7F8E\u80A1\u65F6\u6211\u662F\u8FD9\u6837\u7684\uFF1A\u6211\u770B\u597D\u4E00\u652F\u80A1\u7968\uFF0C\u5148\u9009\u62E9\u5728\u4EC0\u4E48\u4EF7\u4F4D\u4E70\u591A\u5C11\u80A1\uFF0C\u4E70\u8FDB\u53BB\u4EE5\u540E\u5C31\u4E00\u76F4\u76EF\u7740\u76D8\uFF0C\u56E0\u4E3A\u6211<strong>\u8FD8\u6709\u673A\u4F1A\u53CD\u6094</strong>\u554A\u3002\u6211\u4E70\u8FDB\u53BB\u4EE5\u540E\u5C31\u770B\u7740\uFF0C\u5B83\u4E00\u8DCC\u6211\u5C31\u5728\u60F3\u6211\u662F\u4E0D\u662F\u8BE5\u63D0\u524D\u629B\u6389\u6B62\u635F\u3002\u5B83\u4E00\u6DA8\u6211\u53C8\u5728\u60F3\uFF0C\u5B83\u662F\u4E0D\u662F\u5DF2\u7ECF\u6DA8\u5230\u9876\u70B9\u4E86\u8BE5\u629B\u6389\u6B62\u76C8\u3002\u6BCF\u4E00\u6B21\u65B9\u5411\u7684\u53D8\u52A8\u90FD\u4F1A\u5728\u6211\u7684\u5FC3\u91CC\u6380\u8D77\u4E0D\u5C0F\u7684\u6CE2\u6F9C\uFF0C\u4E0D\u65AD\u62CD\u6253\u6211\u7684\u610F\u5FD7\uFF0C\u6298\u78E8\u6211\u7684\u5185\u5FC3\u3002</p><p>\u5728A\u80A1\u91CC\uFF0C\u4F60\u4E00\u5929\u7684\u9009\u62E9\u662F<strong>\u6709\u9650</strong>\u7684\uFF0C\u4F60\u8FDB\u884C\u5B8C\u5F53\u5929\u7684\u4EA4\u6613\u4EE5\u540E\u4F60\u5C31\u53EA\u80FD\u770B\u7740\u3002\u800C\u5728\u7F8E\u80A1\u91CC\uFF0C\u4F60\u4E00\u5929\u7684\u9009\u62E9\u662F<strong>\u65E0\u9650</strong>\u7684\uFF0C\u4F60\u53EF\u4EE5<strong>\u4E0D\u65AD\u5730</strong>\u4E70\u8FDB\u5356\u51FA\u4E70\u8FDB\u5356\u51FA\u3002\u7F8E\u80A1\u7ED9\u4F60\u66F4\u591A\u7684\u9009\u62E9\uFF0C\u540C\u65F6\u7ED9\u4F60\u5E26\u6765\u4E86\u66F4\u591A\u7684\u7126\u8651\u4E0E\u538B\u529B\u3002</p><p>\u6211\u4E0D\u662F\u60F3\u8BF4\u54EA\u4E2A\u5E02\u573A\u597D\u54EA\u4E2A\u5E02\u573A\u574F\uFF0C\u6211\u53EA\u662F\u60F3\u8868\u660E\u7126\u8651\u7684\u6765\u6E90\u662F\u9009\u62E9\u7684\u591A\u6837\u6027\u9020\u6210\u7684\uFF0C\u8D8A\u591A\u7684\u9009\u62E9\u5E26\u6765\u8D8A\u5927\u7684\u7126\u8651\u3002</p><p>\u4EBA\u7C7B\u7684\u4E00\u5927\u5171\u540C\u70B9\u5C31\u662F\u5BB3\u6015\u635F\u5931\uFF0C\u5BB3\u6015\u81EA\u5DF1\u7684\u5229\u76CA\u6CA1\u6709\u6700\u5927\u5316\uFF0C\u8FD9\u5C31\u80FD\u89E3\u91CA\u4E3A\u4EC0\u4E48\u9009\u62E9\u4F7F\u4EBA\u7126\u8651\uFF0C\u4EBA\u9700\u8981\u505A\u51FA\u9009\u62E9\uFF0C\u901A\u5E38\u90FD\u662F\u5728\u51E0\u4E2A\u5BF9\u81EA\u5DF1\u5229\u76CA\u7A0B\u5EA6\u4E0D\u540C\u7684\u9009\u9879\u4E2D\u9009\u62E9\uFF0C\u800C\u4E14\u4E00\u822C\u90FD\u6CA1\u6709\u5B8C\u7F8E\u7684\u9009\u9879\uFF08e.g., \u8FD9\u8863\u670D\u8D85\u597D\u770B\u8D85\u8212\u670D\uFF0C\u4F46\u597D\u8D35\u554A\u3002 \u8FD9\u5DE5\u4F5C\u8D85\u8F7B\u677E\u8D85\u9002\u5408\u6211\uFF0C\u4F46\u5DE5\u8D44\u597D\u4F4E\u554A\u3002\u8BF8\u5982\u6B64\u7C7B\uFF09\u3002\u4E3A\u4E86\u8BA9\u81EA\u5DF1\u7684\u5229\u76CA\u6700\u5927\u5316\uFF0C\u6211\u4EEC\u4F1A\u4E0D\u65AD\u7684\u6BD4\u8F83\u4E0D\u540C\u7684\u9009\u9879\uFF0C\u4ECE\u4E0D\u540C\u9009\u9879\u4E2D\u627E\u5230\u6700\u4F18\u89E3\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u662F\u5F88\u8017\u8D39\u7CBE\u529B\u7684\uFF0C\u9009\u62E9\u7684\u8D8A\u4E45\uFF0C\u8017\u8D39\u7684\u7CBE\u529B\u8D8A\u5927\uFF0C\u4EBA\u4E5F\u5C31\u4F1A\u8D8A\u6765\u8D8A\u7126\u8651\uFF0C\u8D8A\u6765\u8D8A\u70E6\u8E81\u3002</p><p>\u7126\u8651\u5F71\u54CD\u4EBA\u4EEC\u505A\u51FA\u7406\u6027\u7684\u51B3\u5B9A\u3002</p><p>\u4E00\u65E6\u88AB\u9009\u62E9\u6298\u78E8\u4E45\u4E86\uFF0C\u6CE8\u5B9A\u53D8\u5F97\u7126\u8651\uFF0C\u5728\u7126\u8E81\u4E0D\u5B89\u7684\u60C5\u51B5\u4E0B\uFF0C\u4EBA\u4EEC\u7684\u7406\u6027\u601D\u7EF4\u80FD\u529B\u4F1A\u4E0B\u964D\uFF0C\u5BB9\u6613\u51B2\u52A8\uFF0C\u505A\u51FA\u6B20\u601D\u8003\u7684\u51B3\u5B9A\uFF08e.g., \u4F60\u53EF\u80FD\u4E4B\u524D\u7814\u7A76\u4E00\u4E2A\u80A1\u7968\u4E86\u5F88\u4E45\u51B3\u5B9A\u8981\u5728\u8FD9\u4E2A\u80A1\u7968\u8DCC\u523013\u5200\u518D\u4E70\u5165\uFF0C\u4F46\u5F53\u4F60\u770B\u5230\u8FD9\u80A1\u7968\u52BF\u5982\u7834\u7AF9\u7684\u6DA8\u523016\u5200\u7684\u65F6\u5019\uFF0C\u51B2\u52A8\u5730\u653E\u5F03\u81EA\u5DF1\u8FD9\u4E48\u4E45\u7684\u7814\u7A76\u7684\u6210\u679C\uFF0C\u76F4\u63A5\u4E70\u5165\uFF0C\u7ED3\u679C\u8FFD\u9AD8\u4E86\uFF09\u3002\u56E0\u6B64\u5728\u6709<strong>\u66F4\u591A\u9009\u62E9</strong>\u7684\u7F8E\u80A1\u5E02\u573A\u91CC\uFF0C\u4F60\u66F4\u5BB9\u6613\u53D8\u5F97\u7126\u8651\uFF0C\u5E76\u66F4\u5BB9\u6613\u505A\u51FA\u9519\u8BEF\u7684\u64CD\u4F5C\u3002</p><p>\u6240\u4EE5\u8BF4\uFF0C\u5F53\u4F60\u8981\u505A\u51FA\u91CD\u5927\u51B3\u5B9A\u7684\u65F6\u5019\uFF0C\u51B7\u9759\u4E0B\u6765\u601D\u8003\u4E00\u4E0B\uFF1A\u8FD9\u4E2A\u51B3\u5B9A\u662F\u4E0D\u662F\u4E00\u65F6\u51B2\u52A8\u9020\u6210\u7684\uFF0C\u662F\u975E\u7406\u6027\u7684\u5462\uFF1F</p><p>\u5728\u8FD9\u8D8A\u6765\u8D8A\u81EA\u7531\u3001\u7269\u8D28\u79CD\u7C7B\u8D8A\u6765\u8D8A\u7E41\u591A\u7684\u65F6\u4EE3\u91CC\uFF0C\u9009\u62E9\u81EA\u7136\u800C\u7136\u4E5F\u5C31\u4F1A\u53D8\u5F97\u5F88\u591A\uFF0C\u8981\u5982\u4F55\u5728\u8FD9\u4E2A\u65F6\u4EE3\u91CC\u505A\u5230\u4E0D\u518D\u7126\u8651\uFF0C\u4F3C\u4E4E\u662F\u4E00\u4EF6\u6BD4\u8F83\u56F0\u96BE\u7684\u4E8B\u5462\u3002</p><p>TrystanLei</p><p>\u4E8E2018/12/4\u51CC\u6668</p>',32),a=[n];function e(c,g){return t(),o("div",null,a)}var _=p(s,[["render",e],["__file","2018-12-04.html.vue"]]);export{_ as default};
